						endTime
					);
					var obj = {};
					obj.ci_name = metricIds[m].ciName;
					obj.metric_name = metricIds[m].metricName;
					obj.data = metricDataObj;
					returnValues.push(obj);
				}
			}

			return JSON.stringify(returnValues);
		} catch (e) {
			return e;
		}
	},
	queryAnomalyMetric: function (ci_id, metric_name, start_time, end_time) {
		var ciGr = new GlideRecordSecure("cmdb_ci");
		var ci_table = "";
		ciGr.addQuery("sys_id", ci_id);
		ciGr.query();
		if (ciGr.next()) {
			ci_table = ciGr.getValue("sys_class_name");
		}

		var metricTableGr = new GlideRecordSecure(ci_table);
		metricTableGr.addQuery("sys_id", ci_id);
		metricTableGr.query();
		var dataSet = {};

		try {
			//build a transform that returns a simple average
			var builder = new sn_clotho.Transformer(metricTableGr);
			builder.metric(metric_name).avg().label("AVG");
			builder.metric(metric_name).max().label("MAX");
			builder.metric(metric_name).min().label("MIN");

			//execute transform and return result for visualization
			var result = builder.execute(start_time, end_time);

			var avgData = result.getByLabel("AVG");
			var maxData = result.getByLabel("MAX");
			var minData = result.getByLabel("MIN");

			if (!avgData || !maxData || !minData) {
				dataSet.length = 0;
				dataSet.series = [
					{
						type: "AVG",
						data: []
					},
					{
						type: "MAX",
						data: []
					},
					{
						type: "MIN",
						data: []
					}
				];
				/*var intervalInSeconds = 60;
				var timeDifference = GlideDateTime.subtract(start_time, end_time);
				if (timeDifference.getDisplayValue().contains("Days")) {
					intervalInSeconds = 60 * 60;
				}
				while (start_time <= end_time) {
					dataSet.series[0].data.push({time: this.convertSysCreatedStrToMS(start_time), value: 0});
					dataSet.series[1].data.push({time: this.convertSysCreatedStrToMS(start_time), value: 0});
					dataSet.series[2].data.push({time: this.convertSysCreatedStrToMS(start_time), value: 0});
					start_time.addSeconds(intervalInSeconds);
				}*/
				return dataSet;
			}

			var timestamp = avgData.getStart().getNumericValue();
			var period = avgData.getPeriod() * 1000;

			var avgValues = avgData.getValues();
			var maxValues = maxData.getValues();
			var minValues = minData.getValues();

			dataSet.length = avgValues.length;
			dataSet.series = [
				{
					type: "AVG",
					data: []
				},
				{
					type: "MAX",
					data: []
				},
				{
					type: "MIN",
					data: []
				}
			];

			for (var j = 0; j < dataSet.length; j++) {
				var avgDataPoint = {time: timestamp, value: avgValues[j]};
				var maxDataPoint = {time: timestamp, value: maxValues[j]};
				var minDataPoint = {time: timestamp, value: minValues[j]};

				dataSet.series[0].data.push(avgDataPoint);
				dataSet.series[1].data.push(maxDataPoint);
				dataSet.series[2].data.push(minDataPoint);

				timestamp += period;
			}

		} catch(err) {
			return {error: err};
		}
		return dataSet;
	},
	getAlerts: function(ciId, alertType, alertState, sysparam, limit, page, sortBy, sortDirection, startTime, endTime, timerangeColumn, tagFilters) {
		var dataStr="";
		var alertStateFilter="Open,Reopen";
		if (alertState == "All")
			alertStateFilter="Open,Reopen,Closed";
		var sysparam_query="";
		if (sysparam != "")
			sysparam_query = sysparam;
		var cis =[];
		if(alertType=="service")
			cis = this.getAllCIsInService(ciId);
		else if (alertType=="ci")
			cis = this.getValuesByNameMatch(ciId, "|");
		else if (alertType=="none")
			cis=[""];
		var tagSysparam = "";
		if (tagFilters != "") {
			tagSysparam = this.createTagSysparam(tagFilters, 'additional_info');
		}

		for(var i=0;i<cis.length;i++) {
			var alertsbyCI=this.getAlertsByCI(cis[i],alertStateFilter,sysparam_query, limit, page, sortBy, sortDirection, startTime, endTime, timerangeColumn, tagSysparam);
			if (alertsbyCI.length > 0)
				dataStr += alertsbyCI + ",";
		}

		if(dataStr.charAt(dataStr.length - 1)==',')
			dataStr=dataStr.substring(0, dataStr.length - 1);
		return '['+dataStr+"]";
	},
	getTopology: function (serviceId, childDepth, parentDepth, sysparam_query) {
		var excludedClasses = 'dscy_route_next_hop,cmdb_ci_memory_module,cmdb_ci_disk,cmdb_ci_disk_partition,cmdb_ci_config_file_tracked,	cmdb_ci_ip_address,dscy_router_interface,cmdb_ci_network_adapter,dscy_route_interface,cmdb_ci_file_system,cmdb_ci_lvm_pool,	cmdb_ci_storage_device,cmdb_ci_nas_file_system,cmdb_ci_os_packages,sys_package,cmdb_ci_storage_pool,cmdb_ci_patches,cmdb_ci_endpoint_manual';
		var topologyRows='{"rows": [';
		var childDepthLimit = 3;
		var parentDepthLimit = 0;
		if (childDepth != "" && childDepth >= 0)
			childDepthLimit = childDepth;
		if (parentDepth != "" && parentDepth >= 0)
			parentDepthLimit = parentDepth;
		var parentIds = [];
		var originalIds = [];
		if (serviceId != "") {
			originalIds.push(serviceId);
			parentIds.push(serviceId);
		}
		try {
			//Adding starting point node so that all nodes have a parent which can pass classes and alerts down
			var startGR = new GlideRecordSecure("cmdb_ci");
			startGR.addQuery('sys_id', parentIds[0]);
			startGR.query();
			if (startGR.next()) {
				var name = startGR.getDisplayValue("name");
				name = name.replace(/\\/g, "/");
				var className = startGR.getDisplayValue("sys_class_name");
				var classNameValue = startGR.getValue("sys_class_name");
				var sys_id = startGR.getValue("sys_id");
				var alertCount = 0;
				var impact = 0;
				impact = this.getImpactStatusOfCI(parentIds[0]);
				var alertCountResponse = this.getAggregate("em_alert", "state!=closed^cmdb_ci=" + sys_id, "COUNT", "sys_id", "cmdb_ci", "1");
				if (alertCountResponse.length > 0) {
					alertCount = alertCountResponse[0].COUNT;
				}
				var childRow = '["' + className + "||" + classNameValue + '",' + 0 + ',"' + "Starting Point||0" + '","' + name + "||" + sys_id + '",' + impact + ',' + alertCount + '],';
				topologyRows += childRow;
			}

			while (parentDepthLimit > 0) {
				var nextChildIds = [];
				while (parentIds.length > 0) {
					var childId = parentIds.pop();
					var parentGR = new GlideRecordSecure("cmdb_rel_ci");
					parentGR.addQuery("child.sys_id", childId);
					if (sysparam_query != "")
						parentGR.addEncodedQuery(sysparam_query);
					parentGR.addQuery("child.sys_class_name", "NOT IN", this.hard_excluded_classes.toString());
					parentGR.addQuery("parent.sys_class_name", "NOT IN", this.hard_excluded_classes.toString());
					parentGR.query();
					while (parentGR.next()) {
						var parentObj = {};
						parentObj.parent_name = parentGR.parent.name.toString();
						parentObj.parent_name = parentObj.parent_name.replace(/\\/g, "/");
						parentObj.parent_id = parentGR.parent.sys_id.toString();
						parentObj.parent_class = parentGR.parent.sys_class_name.getDisplayValue();
						parentObj.parent_class_value = parentGR.parent.sys_class_name.toString();
						parentObj.child_class = parentGR.child.sys_class_name.getDisplayValue();
						parentObj.child_class_value = parentGR.child.sys_class_name.toString();
						parentObj.child_name = parentGR.child.name.toString();
						parentObj.child_name = parentObj.child_name.replace(/\\/g, "/");
						parentObj.child_id = parentGR.child.sys_id.toString();
						var alertCount = 0;
						var impact = 0;
						impact = this.getImpactStatusOfCI(parentObj.parent_id);
						var alertCountResponse = this.getAggregate("em_alert", "state!=closed^cmdb_ci=" + parentObj.parent_id, "COUNT", "sys_id", "cmdb_ci", "1");
						if (alertCountResponse.length > 0) {
							alertCount = alertCountResponse[0].COUNT;
						}
						var childRow = '["' + parentObj.parent_class + "||" + parentObj.parent_class_value + '",' + 0 + ',"' + parentObj.child_name + "||" + parentObj.child_id + '","' + parentObj.parent_name + "||" + parentObj.parent_id + '",' + impact + ',' + alertCount + '],';
						topologyRows += childRow;
						nextChildIds.push(parentObj.parent_id);
					}
				}
				parentIds=nextChildIds;
				parentDepthLimit -= 1;
			}

			if (parentDepthLimit == 0 && originalIds.length > 0) {
				parentIds=originalIds;
			}

			while (childDepthLimit > 0) {
				var nextParentIds = [];
				while (parentIds.length > 0) {
					var parentId = parentIds.pop();
					var childGR = new GlideRecordSecure("cmdb_rel_ci");
					childGR.addQuery("parent.sys_id", parentId);
					if (sysparam_query != "")
						childGR.addEncodedQuery(sysparam_query);
					childGR.addQuery("child.sys_class_name", "NOT IN", this.hard_excluded_classes.toString());
					childGR.addQuery("parent.sys_class_name", "NOT IN", this.hard_excluded_classes.toString());
					childGR.query();
					while (childGR.next()) {
						var childObj = {};
						childObj.parent_name = childGR.parent.name.toString();
						childObj.parent_name = childObj.parent_name.replace(/\\/g, "/");
						childObj.parent_id = childGR.parent.sys_id.toString();
						childObj.parent_class = childGR.parent.sys_class_name.getDisplayValue();
						childObj.parent_class_value = childGR.parent.sys_class_name.toString();
						childObj.child_id = childGR.child.sys_id.toString();
						childObj.child_name = childGR.child.name.toString();
						childObj.child_name = childObj.child_name.replace(/\\/g, "/");
						childObj.child_class = childGR.child.sys_class_name.getDisplayValue();
						childObj.child_class_value = childGR.child.sys_class_name.toString();
						var alertCount = 0;
						var impact = 0;
						impact = this.getImpactStatusOfCI(childObj.child_id);
						var alertCountResponse = this.getAggregate("em_alert", "state!=closed^cmdb_ci=" + childObj.child_id, "COUNT", "sys_id", "cmdb_ci", "1");
						if (alertCountResponse.length > 0) {
							alertCount = alertCountResponse[0].COUNT;
						}
						var childRow = '["' + childObj.child_class + "||" + childObj.child_class_value + '",' + 0 + ',"' + childObj.parent_name + "||" + childObj.parent_id + '","' + childObj.child_name + "||" + childObj.child_id + '",' + impact + ',' + alertCount + '],';
						topologyRows += childRow;
						nextParentIds.push(childObj.child_id);
					}
				}
				parentIds = nextParentIds;
				childDepthLimit -= 1;
			}

			if(topologyRows.charAt(topologyRows.length - 1)==',')
				topologyRows=topologyRows.substring(0, topologyRows.length - 1);
			topologyRows+="]}";
			return topologyRows;
		} catch (error) {
			return error;
		}
	},
	getAllMetrics: function (sysparam) {
		var gr = new GlideRecordSecure('sa_metric_map');
		var returnStr="";
		if(sysparam.length>0)
			gr.addEncodedQuery(sysparam);
		gr.query();
		while (gr.next())
		{	
			var obj = new Object();
			obj.sys_created_on=gr.getValue("sys_created_on");
			obj.metric_type_id= gr.getDisplayValue("metric_type_id");
			obj.metric_tiny_name= gr.metric_type_id.metric_type_tiny_name.toString();
			obj.ci = gr.getDisplayValue("cmdb_ci");
			obj.resource_id = gr.getDisplayValue("resource_id");
			obj.resource = gr.getDisplayValue("resource");
			obj.source= gr.getDisplayValue("source");
			var jsonString= JSON.stringify(obj);
			returnStr+=jsonString;
			returnStr+=",";

		}
		if (returnStr.charAt(returnStr.length - 1) == ",")
			returnStr=returnStr.substring(0, returnStr.length - 1);

		return '['+returnStr+"]";
	},
	queryGeohashMap: function (table, regionColumn, sysparam) {
		var aggArray = this.getAggregate(table, sysparam, 'COUNT', 'vm_sys_id', regionColumn, 9999);
		for(var i = 0; i < aggArray.length; i++) {
			aggArray[i].geohash = this.getGeohash(aggArray[i][regionColumn]);
		}
		return JSON.stringify(aggArray);
	},
